// Decode bytes to options using version-first bit allocation
function decodeBytes(bytes: number[]) {
  // Version is at bits 0-2, skip it since we're only decoding visual properties
  const skinColor = skinColors[unpack(bytes, 3, 3) % skinColors.length]!
  const hairColor = hairColors[unpack(bytes, 23, 3) % hairColors.length]!
  const hairStyle = hairStyles[unpack(bytes, 6, 5) % hairStyles.length]!(hairColor)
  const eyeType = Object.values(eyeTypes)[unpack(bytes, 11, 4) % Object.values(eyeTypes).length]!
  const mouthType = Object.values(mouthTypes)[unpack(bytes, 15, 4) % Object.values(mouthTypes).length]!
  const facialHairType = facialHair[unpack(bytes, 19, 4) % facialHair.length]!(hairColor)
  const clothingColor = clothingColors[unpack(bytes, 26, 3) % clothingColors.length]!
  const clothingStyle = Object.values(clothingStyles)[unpack(bytes, 29, 4) % Object.values(clothingStyles).length]!(clothingColor)
  const accessoryType = Object.values(accessoryStyles)[unpack(bytes, 37, 3) % Object.values(accessoryStyles).length]!()

  return { skinColor, hairStyle, eyeType, mouthType, facialHairType, clothingColor, clothingStyle, accessoryType }
}
