// Helper to unpack bits from packed bytes
function unpack(bytes: number[], startBit: number, bits: number): number {
  const mask = (1 << bits) - 1
  const byteIndex = Math.floor(startBit / 8)
  const bitOffset = startBit % 8
  return (bytes[byteIndex]! >> bitOffset) & mask
}

